pub enum KanaSet {
    Hiragana,
    Katakana,
    Mixed,
}

impl KanaSet {
    pub fn get_data(&self) -> Vec<Kana> {
        match self {
            KanaSet::Hiragana => get_hiragana(),
            KanaSet::Katakana => get_katakana(),
            KanaSet::Mixed => {
                let mut data = get_hiragana();
                data.extend(get_katakana());
                data
            },
        }
    }
}
